import React, {createContext, useState} from "react";
// Import React and the createContext function from the React library

import all_product from "../Components/Assets/all_product";
// Import all_product from the specified file path

export const ShopContext = createContext(null);
// Create a new context with a default value of null and export it

const getDefaultCart = ()=>{
    let cart = {};
    for (let index = 0; index < all_product.length+1; index++) {
        cart[index] = 0;
    }
    return cart;
}

const ShopContextProvider = (props) => {
    // Define a functional component called ShopContextProvider that takes props as an argument

    const [cartItems, setCartItems] = useState(getDefaultCart());


    const addToCart = (itemId) =>{
        setCartItems((prev)=>({...prev, [itemId]:prev[itemId]+1}))
        console.log(cartItems);
    }
    const removeFromCart = (itemId) =>{
        setCartItems((prev)=>({...prev, [itemId]:prev[itemId]-1}))
    }

    const getTotalCartAmount = () =>{
        let totalAmount = 0;
        for(const item in cartItems)
        {
            if(cartItems[item]>0)
            {
                let itemInfo = all_product.find((product)=>product.id===Number(item))
                totalAmount += itemInfo.new_price * cartItems[item];
            }
        }
        return totalAmount;
    }

    const contextValue ={getTotalCartAmount,all_product, cartItems,addToCart,removeFromCart};

    return (
        <ShopContext.Provider value={contextValue}>
            // Use the ShopContext.Provider component to pass the contextValue to any child components
            {props.children}
            // Render any child components that are passed to ShopContextProvider
        </ShopContext.Provider>
    );
}

export default ShopContextProvider;
// Export the ShopContextProvider component as the default export
